class VDOM_formtexteditor(VDOM_object):

	def render(self, contents=""):
		id = u"o_" + (self.id).replace('-', '_')

		display = u"display:none;" if self.visible == "0" else u"display:block;"

		style = u"""z-index: {zind}; top: {top}px; left: {left}px; overflow: visible; position: absolute;
			width: {width}px; height: {height}px; padding: 2px 5px 2px 5px; {display} font: 14px tahoma
			""".format(zind = self.zindex, top = self.top, left = self.left,
					width = self.width, height = self.height, display = display)

		#style_area = u"""padding: 2px 5px; font: 14px tahoma; width: {width}px; height:{height}px""".format(width = self.width, height = self.height)
		style_area = u"""font: 14px tahoma;"""

		result = u"""
<script type="text/javascript">$q(function(){
	if (typeof %(id)s_editor !== 'undefined') { delete(%(id)s_editor); }
	var %(id)s_a=$q('#%(id)s>textarea');
	%(id)s_editor = %(id)s_a.cleditor({
		width: %(width)s,
		height: %(height)s,
		bodyStyle: "%(style_area)s"
	})[0];
	$q('#%(id)s').parents('form:first').bind("reset",function(){
		%(id)s_a.val('');
		%(id)s_editor.updateFrame();
	});
});</script>
<div id="%(id)s" style="%(style)s">
	<textarea name="%(name)s" tabindex="%(tabind)s" style="%(style_area)s">%(value)s</textarea>
</div>
			""" % {
				"id":         id,
				"name":       self.name,
				"tabind":     self.tabindex,
				"style":      style,
				"style_area": style_area,
				"value":      self.value,
				"width":      self.width,
				"height":     self.height
			}

		return VDOM_object.render(self, contents=result)

	def wysiwyg(self, contents=""):
		import utils.wysiwyg

		image_id = "4541bf6b-4416-e677-ad2a-172266047733"
		result = utils.wysiwyg.get_empty_wysiwyg_value(self, image_id)

		return VDOM_object.wysiwyg(self, contents=result)
